import React, { useState, useEffect } from "react";

function HourlyRateCalculator() {
  const [hours, setHours] = useState("");
  const [minutes, setMinutes] = useState("");
  const [rate, setRate] = useState("");
  const [total, setTotal] = useState(null);

  useEffect(() => {
    const totalHours = parseFloat(hours || 0) + parseFloat(minutes || 0) / 60;
    const totalAmount = totalHours * parseFloat(rate || 0);
    setTotal(totalAmount);
  }, [hours, minutes, rate]);

  return (
    <div>
      <label>Hour</label>
      <br />
      <input
        type="number"
        placeholder="Hours worked"
        value={hours}
        onChange={(e) => setHours(e.target.value)}
      />{" "}
      <br />
      <label>Minute</label>
      <br />
      <input
        type="number"
        placeholder="Minutes worked"
        value={minutes}
        onChange={(e) => setMinutes(e.target.value)}
      />{" "}
      <br />
      <label>Hourly Rate</label>
      <br />
      <input
        type="number"
        placeholder="Hourly rate"
        value={rate}
        onChange={(e) => setRate(e.target.value)}
      />
      {total !== null && <div>Total: ${total.toFixed(2)}</div>}
    </div>
  );
}

export default HourlyRateCalculator;
